import React from 'react';
import ReactTable from 'react-table';
// import connect from 'react-redux/es/connect/connect';
// import matchSorter from 'match-sorter';

const data = [
  {
    id: 123,
    description: 'Screws',
    subpart: false,
    source: 'Swaglord Inc.',
    manufacturingNumber: 123,
    price: 12.15,
    category: 123,
    location: 1111,
    partCount: 1,
    longLead: true,
    notes: "It's a freaking screw"
  }
];

const columns = [
  { Header: 'ID', accessor: 'id' },
  { Header: 'Description', accessor: 'description' },
  { Header: 'Subpart?', accessor: 'subpart' },
  { Header: 'Source', accessor: 'source' },
  { Header: 'Manufacture Part Number', accessor: 'manufacturingNumber' },
  { Header: 'Price', accessor: 'price' },
  { Header: 'Category ID', accessor: 'category' },
  { Header: 'Location ID', accessor: 'location' },
  { Header: 'Part Count', accessor: 'partCount' },
  { Header: 'Long lead?', accessor: 'longLead' },
  { Header: 'Notes', accessor: 'notes' }
];

class PartsTable extends React.Component {
  render() {
    return (
      <ReactTable
        data={data}
        columns={columns}
        filterable
        defaultFilterMethod={(filter, row) =>
          String(row[filter.id]) === filter.value
        }
        defaultPageSize={10}
        style={{ height: '400px' }}
        className="-striped -highlight"
      />
    );
  }
}

export default PartsTable;
