import React from 'react';
import './signin.scss';
import logo from '../../assets/logo.png';
import { Link } from 'react-router-dom';
import superagent from 'superagent';

const minUserName = 5;
const minPassword = 7;

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      password: '',
      errorUser: '',
      errorPassword: ''
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleUser = event => {
    this.setState({ username: event.target.value });
  };

  handlePassword = event => {
    console.log(this.state.password);
    this.setState({ password: event.target.value });
  };

  async handleSubmit(event, loginHandler) {
    event.preventDefault();
    const { username, password } = this.state;
    if (username.length < minUserName) {
      this.setState({ errorUser: `Please ` });
    }
    if (password.length < minPassword) {
    }
    console.log('USERNAME: ', username);
    console.log('PASSWORD: ', password);

    await superagent
      .post(`/signin`)
      .auth(username, password)
      .then(response => {
        const token = response.text;
        loginHandler(token);
      })
      .catch(console.error);
  }

  render() {
    return (
      <div>
        <div className="centered">
          <img src={logo} alt="the logo of fly sorter" />
          <div className="signin">
            <form>
              <li>
                <label type="username">User Name</label>
                <input
                  type="text"
                  placeholder="please enter your username"
                  name="username"
                  onChange={this.handleUser}
                  required
                />
              </li>
              <li>
                <label type="password">Password</label>
                <input
                  type="password"
                  placeholder="please enter your password"
                  name="password"
                  onChange={this.handlePassword}
                  required
                />
              </li>
              <button type="submit" onClick={this.handleSubmit}>
                Login
              </button>
            </form>
          </div>
        </div>
        <p className="base">No account?</p>
        <Link to="/signup">Create an account</Link>
      </div>
    );
  }
} //end class LOGIN

export default Login;
